import cv2
import urllib.request
import numpy as np

bob_image_url = "data:image/jpeg;base64,"
urllib.request.urlretrieve(bob_image_url, "bob.png") # downloads file as "bunny.png"
img = cv2.imread('bob.png',cv2.IMREAD_COLOR)

cv2.line(img,(0,0),(150,150),(255,0,0),3)
cv2.rectangle(img,(15,25),(150,150),(255,255,0),3)
cv2.circle(img,(120,50),30,(0,0,250),-1)

#poligons
pts = np.array([[10,5],[20,30],[70,20],[50,10]],np.int32)
#pts = pts.reshape((-1,1,2))

#False open poligon
cv2.polylines(img,[pts],False,(0,0,0),3)
#True it close poligon
#cv2.polylines(img,[pts],True,(0,0,0),3)

#WRITING
font = cv2.FONT_HERSHEY_COMPLEX
cv2.putText(img,'Test Text',(0,130),font,1,(0,0,0),2,cv2.LINE_AA)




cv2.imshow('bob',img)
cv2.waitKey(0)
cv2.destroyAllWindows()
